/*! hebcal.bundle.min.js - minimal hebcal.HDate support */
(function(global){

  function HDate(date) {
    this.gregDate = date instanceof Date ? date : new Date();
  }

  // Hebrew months (simplified, fixed for example)
  HDate.HEBREW_MONTHS = [
    "ניסן","אייר","סיון","תמוז","אב","אלול",
    "תשרי","חשוון","כסלו","טבת","שבט","אדר"
  ];

  // Dummy renderHebrewDate, returns string כמו "ד' ניסן תשפ״ה"
  HDate.prototype.renderHebrewDate = function(format) {
    const hebMonths = HDate.HEBREW_MONTHS;
    // פשוט נחזיר תאריך עברי מדומה לצורך דוגמה
    const day = this.gregDate.getDate();
    // נחשב חודש עברי פשוט לפי חודש לועזי (לא מדויק!)
    const monthIndex = (this.gregDate.getMonth() + 1) % 12;
    const month = hebMonths[monthIndex];
    // נחשב שנה עברית בערך (נוסף 3760 לשנה לועזית)
    const year = this.gregDate.getFullYear() + 3760;
    // מחזיר תאריך לדוגמה בפורמט פשוט
    return day + " " + month + " " + year;
  };

  // חשוף גלובלי
  global.hebcal = global.hebcal || {};
  global.hebcal.HDate = HDate;

})(typeof window !== "undefined" ? window : this);
